import { useState } from 'react'


export default () => {
  const [todos, setTodos] = useState(['todo 1','todo 2'])
  const [todo, setTodo] = useState('')
  return(
    <>
      <input value={todo} onChange={(e) => setTodos(e.target.value)}/>
      <button onClick={()=> setTodos()}>Adicionar</button>
      {todos.map(todo =>(
        <h1>{todo}</h1>
      ))}
    </>
  )
}
